"""
1769. Minimum Number of Operations to Move All Balls to Each Box

"""

class Solution:
    def minOperations(self, boxes: str):
        box_list=list(boxes)
        res=[]

        for i in range(len(box_list)):
            sum=0
            for j in range(len(box_list)):
                
                if box_list[j]=='1':
                    
                    sum+=abs(i-j)
                    

            res.append(sum)
        return res

s=Solution()
print(s.minOperations(""))
